    paperworkAfterRead(file, fileIndex) {
      console.log("fileIndex ->", fileIndex);
      const that = this;
      _Toast.loading({
        duration: 0,
        message: that.$t("上传中"),
        forbidClick: true
      });
      let formData = new FormData(); //通过formdata上传
      formData.append("file", file.file);
      formData.append("moduleName", "selle");
      uploadimg(formData).then(data => {
        _Toast.clear();
        that.ruleForm[`idimg_${fileIndex}`] = data;
      }).catch(() => {
        _Toast.clear();
        _Toast(that.$t("失败"));
        that[`fileList${fileIndex + 1}`] = [];
      });
      if (fileIndex == 0) {
        uploadimg(formData).then(data => {
          _Toast.clear();
          _Toast(that.$t("成功"));
          that.ruleForm.sellerImg = data;
        }).catch(() => {
          _Toast.clear();
          _Toast(that.$t("失败"));
          that.ruleForm.sellerImg = [];
        });
      }
