import "vant/es/toast/style";
import _Toast from "vant/es/toast";
import "vant/es/nav-bar/style";
import _NavBar from "vant/es/nav-bar";
import "core-js/modules/es.array.push.js";
import { xiugai_zijinmima_post } from "@/API/user";
import { isLogin } from '@/utils/utis';
import Vcode from "vue-puzzle-vcode";
export default {
  data() {
    return {
      img1: require('@/assets/image/slider/1.png'),
      img2: require('@/assets/image/slider/2.png'),
      img3: require('@/assets/image/slider/3.png'),
      img4: require('@/assets/image/slider/4.png'),
      img5: require('@/assets/image/slider/5.png'),
      show: false,
      it: {
        input1: '',
        input2: '',
        input3: ''
      }
    };
  },
  components: {
    [_NavBar.name]: _NavBar,
    Vcode
  },
  created() {
    if (!isLogin()) {
      this.$router.push('/');
    }
  },
  methods: {
    onClickLeft() {
      this.$router.push({
        path: '/Setting',
        dir: 'left'
      });
    },
    biaodantijiao() {
      const t = this;
      if (t.it.input1 === '') {
        _Toast(t.$t('请输入6位数数字原密码'));
        return;
      }
      if (t.it.input2 === '') {
        _Toast(t.$t('请输入6位数数字新密码'));
        return;
      }
      if (t.it.input3 !== t.it.input2) {
        _Toast(t.$t('两次密码输入不一致'));
        return;
      }
      this.submitHandle();
      //   this.show = true
    },
    onSuccess() {
      this.submitHandle();
      this.show = false;
    },
    submitHandle() {
      _Toast.loading({
        duration: 0,
        message: this.$t('提交中'),
        forbidClick: true
      });
      const params = {
        old_safeword: this.it.input1,
        safeword: this.it.input2,
        re_safeword: this.it.input3
      };
      xiugai_zijinmima_post(params).then(async () => {
        _Toast.success(this.$t('修改成功'));
        await this.$store.dispatch('getUserInfo');
        this.onClickLeft();
      }).catch(err => {
        _Toast.clear();
        // Toast.fail(err.msg)
      });
    }
  }
};
