import "vant/es/toast/style";
import _Toast from "vant/es/toast";
import "vant/es/nav-bar/style";
import _NavBar from "vant/es/nav-bar";
import { apiRefreshAvatar } from "@/API/common";
export default {
  components: {
    [_NavBar.name]: _NavBar
  },
  data() {
    return {
      current: 1
    };
  },
  mounted() {
    this.current = this.$route.query?.avatar || 1;
  },
  methods: {
    selectAvatar(item) {
      this.current = item;
      // this.$emit('selectAvatar', item)
    },
    async submitHandle() {
      _Toast.loading({
        duration: 0,
        forbidClick: true,
        message: ""
      });
      await apiRefreshAvatar({
        idx: this.current
      });
      _Toast.clear();
      this.$router.go(-1);
    }
  }
};
