import { apiCheckAvailability } from '@/API/loan';
import { isLogin } from "@/utils/utis";
export default {
  components: {
    Footer,
    [_Toast.name]: _Toast,
    [_Icon.name]: _Icon
  },
  data() {
    return {
      itemName: process.env.VUE_APP_ITEM_NAME,
      worryFreeData: [{
        name: this.$t('灵活额度'),
        desc: this.$t('100,000至30,000,000，额度灵活， 没有限制，最长贷款期限一年。'),
        icon: require('@/assets/image/locn/worry_free1.png'),
        iconName: 'meiyuan9'
      }, {
        name: this.$t('急速放款'),
        desc: this.$t('最快一小时通过审核，审核通过 立马下款。'),
        icon: require('@/assets/image/locn/worry_free2.png'),
        iconName: 'huojianjiasu'
      }, {
        name: this.$t('安全无忧'),
        desc: this.$t('无抵押贷款，无需押金，不打回访电话， 这本就是一件私密的事。'),
        icon: require('@/assets/image/locn/worry_free3.png'),
        iconName: 'dunpai'
      }],
      isCheckAvailability: 'false',
      partnerIcon: [],
      isEn: getStorage('lang') == 'en',
      isCn: getStorage('lang') == 'cn'
    };
  },
  async created() {
    if (['SM-wholesaleShop', 'FamilyShop'].includes(this.itemName)) {
      const paths = ['https://www.jpmorganchase.com/', 'https://www.bankofamerica.com/', 'https://www.wellsfargo.com/', 'https://www.citi.com/', 'https://www.goldmansachs.com/', 'https://www.hsbc.com', 'https://www.ubs.com', 'https://www.credit-suisse.com', 'https://www.barclays.co.uk/', 'https://www.sc.com/en/', 'https://10bestpersonalloans.com/go/pmax-usa-eng-d-g.html#o6', 'https://www.mastercard.com/global/en.html'];
      for (let i = 0; i < 12; i++) {
        this.partnerIcon.push({
          icon: require(`@/assets/image/locn/partner/s${i + 1}.png`),
          path: paths[i]
        });
      }
    } else {
      const paths = ['https://www.cimbclicks.com.my', 'https://www.cimb.com.my', 'https://www.rhbgroup.com', 'https://www.loanstreet.sg/', 'https://www.dbs.com.sg/', 'https://www.graceloanadvance.com', 'https://hk.lendela.com', 'https://epaycash.hk', 'https://www.hangseng.com/', 'https://www.hsbc.com.hk'];
      for (let i = 0; i < 10; i++) {
        this.partnerIcon.push({
          icon: require(`@/assets/image/locn/partner/${i + 1}.jpg`),
          path: paths[i]
        });
      }
    }
    const lang = this.$route.query?.lang;
    const token = this.$route.query?.token;
    token && localStorage.setItem('token', token);
    token && (await this.$store.dispatch('getUserInfo'));
    if (lang) {
      if (lang != localStorage.getItem('lang')) {
        this.$i18n.locale = lang;
        this.setLanguage(lang);
      }
    }
    if (isLogin()) {
      this.requireCheckAvailability();
    }
  },
  methods: {
    ...mapMutations(["setLanguage"]),
    async requireCheckAvailability() {
      this.isCheckAvailability = await apiCheckAvailability();
    },
    handleJump(path) {
      if (window.plus) {
        plus.runtime.openURL(encodeURI(path + '?wt=app'));
        return;
      } else {
        window.open(path);
      }
    },
    handleApply() {
      if (!isLogin()) {
        this.$notifyWarn(this.$t("请先登录"));
        return this.$router.push('/login');
      }
      if (this.isCheckAvailability == 'true') this.$router.push('/loan/apply');
    },
    handleMine() {
      if (!isLogin()) {
        this.$notifyWarn(this.$t("请先登录"));
        return this.$router.push('/login');
      }
      this.$router.push('/loan/my');
    }
  }
};
