import "vant/es/skeleton/style";
import _Skeleton from "vant/es/skeleton";
import "vant/es/action-sheet/style";
import _ActionSheet from "vant/es/action-sheet";
import "vant/es/pull-refresh/style";
import _PullRefresh from "vant/es/pull-refresh";
import "vant/es/list/style";
import _List from "vant/es/list";
import "vant/es/tabs/style";
import _Tabs from "vant/es/tabs";
import "vant/es/empty/style";
import _Empty from "vant/es/empty";
import "vant/es/tab/style";
import _Tab from "vant/es/tab";
import "vant/es/nav-bar/style";
import _NavBar from "vant/es/nav-bar";
import "core-js/modules/es.array.push.js";
import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.map.js";
import { home_sellerGoods } from "@/API/home";
import { priceFormat, priceFormatInt } from '@/utils/utis';
// import { throws } from 'assert';
export default {
  data() {
    return {
      mainColor: process.env.VUE_APP_MAIN_COLOR,
      priceFormat,
      priceFormatInt,
      highLow: 1,
      list: [],
      loading: false,
      finished: false,
      show: false,
      refreshing: false,
      filter_list: [this.$t('综合'), this.$t('销量'), this.$t('价格'), this.$t('新品')],
      filterIndex: 0,
      page_no: 0,
      isPrice: 0,
      newTime: 0,
      isHot: 0,
      actions: [],
      active1: 0,
      active2: 0,
      active3: 0,
      actions1: [{
        name: this.$t('价格由高到低'),
        value: 1
      }, {
        name: this.$t('价格由低到高'),
        value: 2
      }],
      actions2: [{
        name: this.$t('销售量由高到低'),
        value: 1
      }, {
        name: this.$t('销售量由低到高'),
        value: 2
      }],
      actions3: [{
        name: this.$t('时间由远到近'),
        value: 1
      }, {
        name: this.$t('时间由近到远'),
        value: 2
      }],
      shopLoading: true,
      itemName: process.env.VUE_APP_ITEM_NAME
    };
  },
  components: {
    [_NavBar.name]: _NavBar,
    [_Tab.name]: _Tab,
    [_Empty.name]: _Empty,
    [_Tabs.name]: _Tabs,
    [_List.name]: _List,
    [_PullRefresh.name]: _PullRefresh,
    [_ActionSheet.name]: _ActionSheet,
    [_Skeleton.name]: _Skeleton
  },
  // mounted() {
  //     console.log(`$route.query.className ::->`, this.$route.query);
  // },
  created() {
    const tempObj = JSON.parse(sessionStorage.getItem("classificationState"));
    if (['Argos', 'ArgosShop'].includes(this.itemName)) {
      this.filter_list = ['', this.$t('销量'), this.$t('价格'), ''];
      this.selectIndex({}, 1, 3);
    } else if (tempObj?.filterIndex) {
      this.filterIndex = tempObj.filterIndex;
      this.highLow = tempObj.highLow;
      this.selectIndex({}, tempObj.filterIndex, tempObj.highLow);
    } else {
      this.onLoad();
    }
  },
  methods: {
    onRefresh() {
      // 清空列表数据
      this.finished = false;
      this.page_no = 0;
      // 重新加载数据
      // 将 loading 设置为 true，表示处于加载状态
      this.shopLoading = this.loading = true;
      this.onLoad();
    },
    onSelect(item) {
      if (this.filterIndex == 1) {
        this.isPrice = 0;
        this.isHot = item.value;
        this.newTime = 0;
        this.actions2.map(info => {
          info.color = '';
          if (info.value == item.value) {
            info.color = this.mainColor;
          }
        });
      } else if (this.filterIndex == 2) {
        this.isPrice = item.value;
        this.isHot = 0;
        this.newTime = 0;
        this.actions1.map(info => {
          info.color = '';
          if (info.value == item.value) {
            info.color = this.mainColor;
          }
        });
      } else {
        this.isPrice = 0;
        this.isHot = 0;
        this.newTime = item.value;
        this.actions3.map(info => {
          info.color = '';
          if (info.value == item.value) {
            info.color = this.mainColor;
          }
        });
      }
      this.page_no = 0;
      this.list = [];
      this.onLoad();
      document.body.scrollTop = 0;
      document.documentElement.scrollTop = 0;
    },
    onCancel() {
      if (this.filterIndex == 1) {
        this.actions1.map(info => {
          info.color = '';
        });
        this.isPrice = 0;
      } else if (this.filterIndex == 2) {
        this.actions2.map(info => {
          info.color = '';
        });
        this.isHot = 0;
      } else {
        this.actions3.map(info => {
          info.color = '';
        });
        this.newTime = 0;
      }
      this.page_no = 0;
      this.list = [];
      this.onLoad();
      document.body.scrollTop = 0;
      document.documentElement.scrollTop = 0;
    },
    onLoad() {
      // this.shopLoading = true
      if (this.refreshing) {
        this.list = [];
        this.refreshing = false;
      }
      this.tuanduimingxi_post();
    },
    tuanduimingxi_post() {
      this.page_no++;
      var data = {
        'pageNum': this.page_no,
        'pageSize': 10,
        'categoryId': this.$route.query.categoryId,
        'isPrice': this.isPrice,
        'newTime': this.newTime,
        'isHot': this.isHot
      };
      !data.categoryId && delete data.categoryId;
      !data.isHot && delete data.isHot;
      !data.isPrice && delete data.isPrice;
      !data.newTime && delete data.newTime;
      home_sellerGoods(data).then(res => {
        for (let i = 0; i < res.pageList.length; i++) {
          this.list.push(res.pageList[i]);
        }
        this.shopLoading = this.loading = false;
        if (res.pageList.length == 0) {
          this.finished = true;
        }
      });
    },
    openDetails(item) {
      document.body.scrollTop = 0;
      document.documentElement.scrollTop = 0;
      this.$router.push('/CommodityDetails?sellerGoodsId=' + item.id);
    },
    onClickLeft() {
      this.$router.back();
    },
    selectIndex(item, index, highLow) {
      this.shopLoading = true;
      if (!highLow) {
        if (this.filterIndex === index) {
          this.highLow = this.highLow === 2 ? 3 : this.highLow === 3 ? 2 : 2;
        } else {
          this.highLow = 2;
        }
      } else {
        this.highLow = highLow;
      }
      this.filterIndex = index;
      if (index == 1) {
        // this.isPrice = 0
        // this.newTime = ''
        this.actions = this.actions2;
        this.onSelect({
          value: this.highLow - 1
        });
      } else if (index == 2) {
        this.actions = this.actions1;
        // this.isPrice = 1
        // this.newTime = ''
        this.onSelect({
          value: this.highLow - 1
        });
      } else if (index == 3) {
        // this.isPrice = 0
        // this.newTime = 'new'
        this.actions = this.actions3;
        this.onSelect({
          value: this.highLow - 1
        });
      } else {
        this.isPrice = 0;
        this.newTime = '';
        this.isPrice = 0;
        this.newTime = 0;
        this.isHot = 0;
        this.page_no = 0;
        this.list = [];
        this.onLoad();
        document.body.scrollTop = 0;
        document.documentElement.scrollTop = 0;
      }
      this.filterIndex = index;
      sessionStorage.setItem("classificationState", JSON.stringify({
        filterIndex: this.filterIndex,
        highLow: this.highLow
      }));
    }
  }
};
