import "vant/es/toast/style";
import _Toast from "vant/es/toast";
import "vant/es/popup/style";
import _popup from "vant/es/popup";
import "vant/es/empty/style";
import _Empty from "vant/es/empty";
import "vant/es/picker/style";
import _Picker from "vant/es/picker";
import "vant/es/icon/style";
import _Icon from "vant/es/icon";
import "vant/es/pull-refresh/style";
import _PullRefresh from "vant/es/pull-refresh";
import "vant/es/list/style";
import _List from "vant/es/list";
import "vant/es/nav-bar/style";
import _NavBar from "vant/es/nav-bar";
import "core-js/modules/es.array.push.js";
import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.reduce.js";
import { tixian_jilu_post } from "@/API/user";
import { isLogin, priceFormat } from '@/utils/utis';
export default {
  data() {
    return {
      list: [],
      loading: false,
      finished: false,
      refreshing: false,
      page_no: 0,
      content_type: 'brokerage',
      // columns: [this.$t('全部'), this.$t('recharge'), this.$t('withdraw'), this.$t('brokerage'), this.$t('sys-increment'), this.$t('sys-decrement'), this.$t('vip-recharge'), this.$t('freeze-order'), this.$t('unfreeze-order'), this.$t('pay-order'), this.$t('bonus')],
      // columns2: ['', 'recharge', 'withdraw', 'brokerage', 'sys-increment', 'sys-decrement', 'vip-recharge', 'freeze-order', 'unfreeze-order', 'pay-order', 'bonus'],
      showPicker: false,
      shaixuan_index: 0,
      priceFormat
    };
  },
  components: {
    [_NavBar.name]: _NavBar,
    [_List.name]: _List,
    [_PullRefresh.name]: _PullRefresh,
    [_Icon.name]: _Icon,
    [_Picker.name]: _Picker,
    [_Empty.name]: _Empty,
    [_popup.name]: _popup
  },
  created() {
    if (!isLogin()) {
      this.$router.push('/');
    }
    this.libiao_post();
  },
  methods: {
    copyData(ea) {
      //如果需要回调：
      this.$copyText(ea).then(e => {
        _Toast(this.$t('复制成功'));
        console.log(e);
      }, function (e) {
        _Toast(this.$t('复制失败'));
        console.log(e);
      });
    },
    libiao_post() {
      this.page_no++;
      tixian_jilu_post({
        page_no: this.page_no
      }).then(res => {
        if (res.elements.length == 0) {
          this.finished = true;
        } else {
          this.loading = false;
          this.list = [...this.list, ...res.elements];
        }
        var obj = {};
        this.list = this.list.reduce((prev, cur) => {
          obj[cur.order_no] ? "" : obj[cur.order_no] = true && prev.push(cur);
          return prev;
        }, []);
      });
    },
    onClickLeft() {
      this.$router.push({
        path: '/withdraw',
        dir: 'left'
      });
      // this.$router.go(-1)
    },
    onLoad() {
      if (this.refreshing) {
        this.list = [];
        this.refreshing = false;
      }
      this.libiao_post();
    },
    onRefresh() {
      // 清空列表数据
      this.finished = false;
      this.page_no = 0;
      // 重新加载数据
      // 将 loading 设置为 true，表示处于加载状态
      this.loading = true;
      this.onLoad();
    },
    openDeital(order_no, failure_msg) {
      this.$router.push({
        path: '/withdrawRecordDetails',
        query: {
          order_no,
          failure_msg
        }
      });
    }
  }
};
