import "vant/es/image-preview/style";
import _ImagePreview from "vant/es/image-preview";
import "vant/es/toast/style";
import _Toast from "vant/es/toast";
import "vant/es/nav-bar/style";
import _NavBar from "vant/es/nav-bar";
import { rechargeBlockchainDetails } from "@/API/user";
import { getImgUrl, priceFormat } from '@/utils/utis';
import QRCode from 'qrcode';
export default {
  name: 'rechargeRecordDetails',
  components: {
    [_NavBar.name]: _NavBar,
    [_Toast.name]: _Toast,
    [_ImagePreview.Component.name]: _ImagePreview.Component
  },
  data() {
    return {
      itemName: process.env.VUE_APP_ITEM_NAME,
      pageDone: false,
      orderNo: null,
      dataInfo: null,
      previewShow: false,
      previewImages: [],
      codeImgUrl: '',
      priceFormat
    };
  },
  mounted() {
    this.$nextTick(() => {
      const {
        order_no,
        failure_msg
      } = this.$route.query;
      if (order_no) {
        this.orderNo = order_no;
        this.failureMsg = failure_msg;
        this.getDataInfo();
      } else {
        _Toast(this.$t('订单号不存在'));
      }
    });
  },
  methods: {
    onClickLeft() {
      this.$router.go(-1);
    },
    copyData(ea) {
      this.$copyText(ea).then(() => {
        _Toast(this.$t('复制成功'));
      }, () => {
        _Toast(this.$t('复制失败'));
      });
    },
    getDataInfo() {
      _Toast.loading({
        duration: 0,
        message: this.$t('请求中')
      });
      rechargeBlockchainDetails({
        order_no: this.orderNo
      }).then(res => {
        res.stateStr = '';
        res.className = '';
        if (res.state === 0) {
          res.stateStr = this.$t('处理中');
        }
        if (res.state === 1) {
          res.className = 'success';
          res.stateStr = this.$t('成功');
        }
        if (res.state === 2) {
          res.className = 'fail';
          res.stateStr = this.$t('失败');
        }
        if (res.img) {
          this.previewImages = [res.img];
        }
        this.dataInfo = res;
        this.pageDone = true;
        _Toast.clear();
        this.$nextTick(() => {
          this.createCodeImg(res.from);
        });
      }).catch(err => {
        // Toast(err.msg)
        _Toast.clear();
      });
    },
    // 生成二维码
    createCodeImg(txt) {
      const that = this;
      const canvas = document.getElementById('canvas');
      QRCode.toCanvas(canvas, txt, {
        width: 124,
        height: 124,
        margin: 0
      }, function (error) {
        if (error) {
          // console.error(error)
        } else {
          that.codeImgUrl = canvas.toDataURL('image/png');
        }
      });
    },
    saveCodeImg() {
      let myCanvas = document.getElementsByTagName('canvas');
      this.imgUrl = myCanvas[0].toDataURL('image/png');
      this.getUrlBase64(this.imgUrl).then(base64 => {
        let link = document.createElement('a');
        link.href = base64;
        link.download = 'qrCode.png';
        link.click();
      });
    },
    getUrlBase64(url) {
      return new Promise(resolve => {
        let canvas = document.createElement('canvas');
        let ctx = canvas.getContext('2d');
        let img = new Image();
        img.crossOrigin = 'Anonymous'; //允许跨域
        img.src = url;
        img.onload = function () {
          canvas.height = 300;
          canvas.width = 300;
          ctx.drawImage(img, 0, 0, 300, 300);
          let dataURL = canvas.toDataURL('image/png');
          canvas = null;
          resolve(dataURL);
        };
      });
    },
    getImgUrlHandle(path) {
      console.log(`path ::->`, path);
      return getImgUrl(path);
    },
    toPay(path) {
      window.open(path);
    }
  }
};
